





CREATE TABLE `temp_weighscale` (
	`Ids` INT(11) NOT NULL AUTO_INCREMENT,
	`MachineNo` VARCHAR(50) NULL DEFAULT NULL COLLATE 'utf8_unicode_ci',
	`MachineIP` VARCHAR(50) NULL DEFAULT NULL COLLATE 'utf8_unicode_ci',
	`MachineDate` DATE NULL DEFAULT NULL,
	`MachineTime` TIME NULL DEFAULT NULL,
	`TareWt` DECIMAL(10,3) NULL DEFAULT 0.000,
	`NetWt` DECIMAL(10,3) NULL DEFAULT 0.000,
	`GrossWt` DECIMAL(10,3) NULL DEFAULT 0.000,
	`LotNo` VARCHAR(50) NULL DEFAULT NULL COLLATE 'utf8_unicode_ci',
	`BaleNo` VARCHAR(50) NULL DEFAULT NULL COLLATE 'utf8_unicode_ci',
	`cDate` DATE NULL DEFAULT NULL,
	`cTime` TIME NULL DEFAULT NULL,
	`cUserId` INT(11) NULL DEFAULT 0,
	`IsImported` INT(1) NULL DEFAULT 0,
	PRIMARY KEY (`Ids`)
)
COLLATE='utf8_unicode_ci'
ENGINE=InnoDB;